import React, { Component } from "react";
import './Home.css';


export default class Home extends Component {
    slideInterval = 5000;
    constructor(props) {

    }
     getFigures() {
        return document.getElementById('carousel').getElementsByTagName('figure');
    }
     moveForward() {
         var pointer = 0
        var figures = this.getFigures();
        for (var i = 0; i < figures.length;) {
            if (figures[i].className === 'visible') {
                figures[i].className = 'hidden';
                pointer = i;
            }
        }

        figures[pointer].className = 'visible';
        setTimeout(this.moveForward, this.slideInterval);
    }
     startPlayback() {
        setTimeout(this.moveForward, this.slideInterval);
    }

    componentDidMount() {
        this.startPlayback();

    }
    render() {
        return <div>
            <div id="carousel">
                <figure class="visible">
                    <img class="slideshow" src="./img/image1.jpeg" alt="img" />
                </figure>
                <figure class="hidden">
                    <img class="slideshow" src="./img/image4.jpg" alt="img" />
                </figure>
                <figure class="hidden">
                    <img class="slideshow" src="./img/image5.jpg" alt="img" />
                </figure>
            </div>

        </div>

    }
}
