import React, { Component } from "react";
import './Home.css';


export default class Home extends Component {
    slideInterval = 5000;
     getFigures() {
        return document.getElementById('carousel').getElementsByTagName('figure');
    }
     moveForward() {
        var pointer = 0;
        var figures = getFigures();
        for (var i = 0; i < figures.length;) {
            if (figures[i].className === 'visible') {
                figures[i].className = 'hidden';
                pointer = i;
            }
        }

        figures[pointer].className = 'visible';
        setTimeout(moveForward, slideInterval);
    }
     startPlayback() {
        setTimeout(moveForward, slideInterval);
    }

    componentDidMount() {
        var slideInterval = 5000; //5 seconds.
        startPlayback();

    }
    render() {
        return <div>
            <div id="carousel">
                <figure class="visible">
                    <img class="slideshow" src="./img/image1.jpeg" alt="img" />
                </figure>
                <figure class="hidden">
                    <img class="slideshow" src="./img/image4.jpg" alt="img" />
                </figure>
                <figure class="hidden">
                    <img class="slideshow" src="./img/image5.jpg" alt="img" />
                </figure>
            </div>

        </div>

    }
}
