import React, { Component } from "react";
import './Home.css';


export default class Home extends Component {
    constructor(props) {
        super(props);
        this.carouselImages = ['image1.jpeg', 'image4.jpg', 'image5.jpg'];
        this.slideInterval = 5000;
        // this.getFigures = this.getFigures.bind(this);
        this.moveForward = this.moveForward.bind(this);
        this.startPlayback = this.startPlayback.bind(this);
    }
    //  getFigures() {
    //     return document.getElementById('carousel').getElementsByTagName('figure');
    // }
     moveForward() {
         var pointer = 0
        var figures = this.getFigures();
        // for (var i = 0; i < figures.length;) {
        //     if (figures[i].className === 'visible') {
        //         figures[i].className = 'hidden';
        //         pointer = i;
        //     }
        // }

        figures[pointer].className = 'visible';
        setTimeout(this.moveForward, this.slideInterval);
    }
     startPlayback() {
        setTimeout(this.moveForward, this.slideInterval);
    }

    componentDidMount() {
        this.startPlayback();
    }
    render() {
        return <div>
            <div id="carousel">
                {this.carouselImages.map(image => {
          // map works like for Each.
                    return (
                        <figure className="visible">
                        <img className="slideshow" src={image} alt="img" />
                    </figure>
          )
        })}
            </div>

        </div>

    }
}
