import React, { Component } from "react";
import './Home.css';


export default class Home extends Component {
    constructor(props) {
        super(props);
        this.carouselImages = ['image1.jpeg', 'image4.jpg', 'image5.jpg'];
        this.slideInterval = 5000;
        this.figures;
        this.getFigures = this.getFigures.bind(this);
        this.moveForward = this.moveForward.bind(this);
        this.startPlayback = this.startPlayback.bind(this);
    }
     getFigures() {
        return document.getElementById('carousel').getElementsByTagName('figure');
    }
     moveForward() {
         var pointer = 0
        for (var i = 0; i < this.figures.length;) {
            if (this.figures[i].className === 'visible') {
                this.figures[i].className = 'hidden';
                pointer = i;
            }
            if (pointer === this.figures.length - 1) {
                pointer = 0;
            }

        }

        this.figures[pointer].className = 'visible';
        setTimeout(this.moveForward, this.slideInterval);
    }
     startPlayback() {
        setTimeout(this.moveForward, this.slideInterval);
    }

    componentDidMount() {
        this.startPlayback();
        this.figures=this.getFigures()
    }
    render() {
        return <div>
            <div id="carousel">
                {this.carouselImages.map(image => {
          // map works like for Each.
                    return (
                        <figure className="visible">
                        <img className="slideshow" src={image} alt="img" />
                    </figure>
          )
        })}
            </div>

        </div>

    }
}
