import React, { component } from 'react';
import './Product.css';
import { render } from '@testing-library/react';

export default function PriceFilter(props) {
    this.state = {
        high: 40,
        low: 0
    }
    this.handleChange = (event) => {
        if (event.target.name === "price-low") {
            this.setState({
                high: this.state.high,
                low: event.target.value
            })
        }
        else if (event.target.name === "price-high") {
            this.setState({
                high: event.target.value,
                low:  this.state.low
            })
        }
    }
    this.handleChange = this.handleChange.bind(this);

    return (
        <div>
            <h4>Price</h4>
            <label for="price-low">Low</label>
            <input type="text" name="price-low" value={this.state.low} onChange={this.handleChange} />

            <label for="price-high">High</label>
            <input type="text" name="price-high" value={this.state} onChange={this.handleChange} />
        </div>
    )
};
